import React, { useState, useEffect } from "react";
import ImageGallery from "react-image-gallery";
import getTrendMoviesPics from "../services/getTrendMoviesPics";

const Slider = () => {
  const [trendMoviesData, setTrendMoviesData] = useState(false);

  const images = [];

  useEffect(() => {
    async function fetchData(){
      if(localStorage.slider){
        setTrendMoviesData(JSON.parse(localStorage.slider))
      }
      if (!localStorage.slider) {
        await getTrendMoviesPics("day")
        .then((result) => setTrendMoviesData(result))
        localStorage.slider = JSON.stringify(trendMoviesData)
        console.log(trendMoviesData)
      }
    }
    fetchData()

  }, []);
  console.log(trendMoviesData)

  if (trendMoviesData) {
    console.log(trendMoviesData)

    for (let i = 0; i < 4; i++) {
      images.push({
        original:
          "https://image.tmdb.org/t/p/original" +
          trendMoviesData[i].backdrop_path,
      });
    }
  }

  return (
    <>
      <ImageGallery
        showPlayButton={false}
        showFullscreenButton={false}
        slideInterval={8000}
        slideDuration={1000}
        autoPlay={true}
        showThumbnails={false}
        items={images}
      />
    </>
  );
};

export default Slider;
