import { useParams } from "react-router-dom"

import { useEffect, useState } from "react";
import Header from "../components/Header";
import search from "../services/Search";
import Movie from "../components/Movie";





const SearchResults = () => {

    const [results, setResults] = useState(undefined);

    const { query } = useParams();
    
    
    useEffect(()=> {
        // console.log(search(query))
        search(query).then(e => {
            setResults(undefined)  // si no es así pilla la data anterior
            console.log(e)
            setResults(e)
        })
    }, [query])

    console.log(results, query)
    

    return(
        <>
            <Header></Header>
            <div className="movie-container">

                {
                results ? results.slice(0, 20).map(e => (
                <Movie key={e.id} rate={e.vote_average} id={e.id} image={`https://image.tmdb.org/t/p/original${e.poster_path}`} alt={e.original_title}></Movie>
                ))
                : `loading... ${query}`
                }

            </div>
        </>
    )
}

export default SearchResults
