import React, { useState, useEffect } from 'react';
import ImageGallery from 'react-image-gallery';


const images = [
    {
      original: 'https://image.tmdb.org/t/p/original/9n5e1vToDVnqz3hW10Jdlvmzpo0.jpg',
    },
    {
      original: 'https://image.tmdb.org/t/p/original/14wIOjYCtfbO9EHTqCbiU9wncMz.jpg',
    },
    {
      original: 'https://image.tmdb.org/t/p/original/skL7c4ZhZqo1IFbMcHNrol4fvkc.jpg',
    },
  ];


const Slider = () => {

    const [trendMoviesData, setTrendMoviesData] = useState([])

    useEffect(() => {
        fetch("https://api.themoviedb.org/3/trending/movie/day?api_key=c6b78e19f7ba833c205511b80032a27c")
        .then(async response => await response.json())
        .then(data => setTrendMoviesData(data))
      }, []);

    if (trendMoviesData.results){
        for (let i = 0; i < 4; i++) {
            console.log(trendMoviesData.results[i])
        }
    };
       


