        //     return movies;
        //   }
        //   fetchMoviesJSON().then(movies => {
        //     setTrendMoviesData(movies); // fetched movies
        //   });


    return(
        <>
            <ImageGallery slideInterval={10000} slideDuration={1000} autoPlay={true} showThumbnails={false} items={images} />
       </>
    )
}

export default Slider
