import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Header from "../components/Header";
import accessMovieData from "../services/accessMovieData";

const MovieDetails = () => {
  const [details, setDetails] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    accessMovieData(id).then((data) => setDetails(data));
  }, []);

  console.log(details);

  const date = details ? details.release_date : "";
  const correctedDate = date? date.substring(0,4):"";

  return (
    <>
      <Header></Header>

      <div className="global-container">
        <div className="film-container">
          <div>
              <h1 className="title">{details.title}</h1>
          </div>
          <div>
            <h2 className="subtitle">
              Titulo original: {details.original_title}
            </h2>
            <ul>
              <li>{details.original_title}</li>
              {/* <li>{details.tagline}</li> */}
              <li>{correctedDate}</li>
            </ul>
          </div>

          <section className="media">
            <section className="pictures">
              <img
                src={`https://image.tmdb.org/t/p/original${details.poster_path}`}
                alt={details.original_title}
                className="poster"
              ></img>
              <img
                src={`https://image.tmdb.org/t/p/original${details.backdrop_path}`}
                alt={details.original_title}
                className="backd"
              ></img>
            </section>
          </section>
        </div>
      </div>
    </>
  );
};

export default MovieDetails;
