import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { API_KEY, API_URL } from '../services/getTrendMoviesPics';
import Header from '../components/Header';
import accesMovieData from '../services/accesMovieData';



const MovieDetails = () =>{
    const [details, setDetails] = useState([])
    const {id} = useParams()

 
    useEffect(()=>{
        accesMovieData(id).then(data => setDetails(data))
    },[])

    // console.log(details)
    
    return (
        <>
            <Header></Header>

            <div> 
                
                <h1>{details.title}</h1>

                <div>
                    <h2>Titulo original: {details.original_title}</h2>
                    <ul>
                        <li>{details.original_title}</li> 12}}
                    </ul>
                </div>
            </div>
            
         

        </>
    )
}

export default MovieDetails
