import { useState, useEffect } from "react";
import { API_KEY, API_URL } from "./Slider";
import Movie from "./Movie";

const Movies = () => {

    const [mainMovies, setMainMovies] = useState([])

    useEffect(() => {
        fetch(`${API_URL}/trending/movie/week${API_KEY}&page=1`)
        .then(async response => await response.json())
        .then(data => setMainMovies(data))


        async function fetchMetaData() {
            let allData = [];
            let morePagesAvailable = true;
            let currentPage = 0;
          
            while(morePagesAvailable) {
              currentPage++;
              const response = await fetch(`${API_URL}/trending/movie/week${API_KEY}&page=${currentPage}`)
              let data = await response.json();
              data.forEach(e => allData.unshift(e));
              morePagesAvailable = currentPage < 1000;
            }

          
            return allData;
        }

        const data = fetchMetaData();
        console.log(data)

        // setMainMovies(movies)

        
    }, []);

    console.log(mainMovies)

    return(
        <div className="movie-container">
            {mainMovies.map(e => (
            <Movie image={`https://image.tmdb.org/t/p/original${e.poster_path}`} alt={e.original_title}></Movie>
            ))}
        </div>
    )
}


export default Movies
