import { useState } from "react";
import { Link } from "react-router-dom"


useState



const Movie = (prop) => {

    const [isFocus, setIsFocus] = useState(false);

    isFocus?console.log(prop.id):null;
    
    return(
        <>
             
        
            <Link to={`/${prop.id}`}>
                <div className='movie' onMouseEnter={() => setIsFocus(true)} onMouseLeave={() => setIsFocus(false)}>
                    <label className="label"><i class="bi bi-star-fill"></i><span className="rate">{prop.rate}</span><span className="smallrate">/10</span></label>
                    <img src={prop.image} alt={prop.alt}></img>
                </div>
            </Link>
           
  
        </>
    )
}

export default Movie
