import { useState, useEffect } from "react";
import { API_KEY, API_URL } from "./Slider";
import Movie from "./Movie";

const Movies = () => {

    const [mainMovies, setMainMovies] = useState([])
    const allMovies = []

    useEffect(() => {

        for (let a = 1; a < 3; a++) {
            fetch(`${API_URL}/trending/movie/week${API_KEY}&page=${a}`)
            .then(async response => await response.json())
            .then(data => {

            for (let i = 0; i < data.results.length; i++) {
                allMovies.push(data.results[i])
            }
        })
        }

        setMainMovies(allMovies)

        
    }, []);

    console.log(mainMovies)

    return(
        <div className="movie-container">
            {mainMovies.map(e => (
            <Movie image={`https://image.tmdb.org/t/p/original${e.poster_path}`} alt={e.original_title}></Movie>
            ))}
        </div>
    )
}


export default Movies
