import { FetchError } from 'node-fetch';
import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import ImageGallery from 'react-image-gallery';


const images = [
    {
      original: 'https://picsum.photos/id/1018/1000/600/',
    },
    {
      original: 'https://picsum.photos/id/1015/1000/600/',
    },
    {
      original: 'https://picsum.photos/id/1019/1000/600/',
    },
  ];


const Slider = () => {

    const [trendMoviesData, setTrendMoviesData] = useState([])

    fetch("https://api.themoviedb.org/3/trending/movie/day?api_key=c6b78e19f7ba833c205511b80032a27c")
        .then(response => response.json())
        .then(data => setTrendMoviesData(data))

        {console.log(trendMoviesData.results[1])}

        for (let i = 0; i < 3; i++) {
            console.log(trendMoviesData.results[i].poster_path) 
        }

    return(
        <>
       <ImageGallery originalHeight={"500px"} slideInterval={10000} slideDuration={1000} autoPlay={true} showThumbnails={false} items={images} />
       
       </>
    )
}

export default Slider
