import React, { useState, useEffect } from 'react';
import Panel from "./Panel"




const Panels = (prop) => {
    
    const providers = [
        {
            name: "Netflix",
            src: "../source/netflix.jpg"
        },
        {
            name: "Amazon Prime",
            src: "../source/primevideo.png"
        },
        {
            name: "HBOMax",
            src: "../source/hbo.jpg"
        },
    
    ]
    
       
    return(
        <>
            {providers.map(provider => {
                <Panel image={provider.src} alt={provider.name}></Panel>
            })}
        </>
    )
}

export default Panels
