import { useState, useEffect } from "react";
import { API_KEY, API_URL } from "./Slider";
import Movie from "./Movie";

const Movies = () => {

    const [mainMovies, setMainMovies] = useState([])
    let allMovies = []
    let pages = 0;
    let dataLength = 0;
    let dataFromApi

    useEffect(() => {

        async function fetchMovies(pages) {
            const response = await fetch(
              `${API_URL}/trending/movie/week${API_KEY}&page=${pages}`
            );
            const data = await response.json();
            return data;
          }
          


        while(dataLength == 0){
            pages++
            
            
            dataFromApi = fetchMovies(pages)
                
            dataLength = dataFromApi.dataLength;

            allMovies.push(dataFromApi)

            setMainMovies(allMovies)
            
        }
        

        // setMainMovies(allMovies)

        
    }, []);

    console.log(mainMovies)

    return(
        <div className="movie-container">
            {mainMovies.map(e => (
            <Movie image={`https://image.tmdb.org/t/p/original${e.poster_path}`} alt={e.original_title}></Movie>
            ))}
        </div>
    )
}


export default Movies
