import React, { useEffect, useState } from "react";
import getReviews from "../services/getReviews";
import Comment from "./Comment";

const Comments = ({ id }) => {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    async function fetchData() {
      await getReviews(id).then((data) => setComments(data.results));
    }
    fetchData();
  }, []);

  console.log(comments)
  return (
    <ul>
      {
        comments
        ? 
        comments.map((e)=> (
            <Comment name={e.author_details.name}  username={e.author} avatar={e.author_details.avatar_path.substring(1,)} date={e.created_at} content={e.content} rating={e.author_details.rating}/>
        ))
        :
        ""
      }
    </ul>
  );
};

export default Comments;
