import "./App.css";
import "./normalize.css";

import MovieDetails from "./pages/MovieDetails";

import { Route, Routes } from "react-router-dom";
import Home from "./pages/Home";
import ProviderDetail from "./pages/ProvidersDetails";
import SearchResults from "./pages/SearchResults";
import { useGlobalState } from "./global/globalStates";

function App() {
  const query = useGlobalState("search")

  return (
    <>
      <Routes>
        <Route path="/" element={<Home />}></Route>
        <Route path="/:id" element={<MovieDetails />}></Route>
        <Route path="/providers/:name" element={<ProviderDetail />}></Route>
        <Route path="/search/" element={<SearchResults query={query} />}></Route>
      </Routes>
    </>
  );
}

export default App;
