import React from 'react'
import "../styles/comment.css"
 
const Comment = ({name,  username, avatar = "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/271deea8-e28c-41a3-aaf5-2913f5f48be6/de7834s-6515bd40-8b2c-4dc6-a843-5ac1a95a8b55.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..BopkDn1ptIwbmcKHdAOlYHyAOOACXW0Zfgbs0-6BY-E", date, content, rating}) => {

    if (avatar.length < 15){
        avatar = `https://image.tmdb.org/t/p/original/${avatar}`
    }

  return (
    <li className='comment-element'>
 
       <div className='comment-user'>
            <img src={avatar} alt={name}/>
            <p>{username}</p>
       </div>
       <div className='comment-content'>
            <p>{content}</p>
       </div>
 
    </li>
  )
}
 
export default Comment
